<?php/* * Adds a contact */require_once 'cms/icontact/config.php';require_once 'cms/icontact/util.php';/***********************************//** set your account details here **//***********************************///$accountId      = 1001413;//$clientFolderId = 9196527;$response = callResource("/a/{$accountId}/c/{$clientFolderId}/contacts",	'POST', array(		array(			'firstName' => '',			'lastName'  => '',			'email'     => $email,		)	));if ($response['code'] == STATUS_CODE_SUCCESS) {	//echo "<h1>Success</h1>\n";		$contactId = $response['data']['contacts'][0]['contactId'];		$warningCount = 0;	if (!empty($response['data']['warnings'])) {		$warningCount = count($response['data']['warnings']);	}		//alert ("<p><b>Success!</b> Added contact {$contactId}, with {$warningCount} warnings.</p>", true);	echo "<script language='javascript' type='text/javascript'>alert('Success. Thank you for signing up to receive our newsletter promotions.');</script>";		//dump($response['data']);} else {	//echo "<h1>Error</h1>\n";		//alert ("<p><b>Error Code:</b> {$response['code']}</p>", false);	echo "<script language='javascript' type='text/javascript'>alert('Error. There was an error trying to insert your email address. Please try again.');</script>";		//dump($response['data']);}if ($lacrosse == "on") {	// now put them into the list	$response = callResource("/a/{$accountId}/c/{$clientFolderId}/subscriptions",		'POST', array(			array(				'contactId' => $contactId,				'listId'  	=> $listId_Lacrosse,				'status'    => 'normal',			)		));}if ($hockey == "on") {	// now put them into the list	$response = callResource("/a/{$accountId}/c/{$clientFolderId}/subscriptions",		'POST', array(			array(				'contactId' => $contactId,				'listId'  	=> $listId_Hockey,				'status'    => 'normal',			)		));}?>