<?php/* * Deletes a contact */require_once '../cms/icontact/config.php';require_once '../cms/icontact/util.php';/***********************************//** set your account details here **//***********************************///$accountId      = 1001413;//$clientFolderId = 9196527;$response = callResource("/a/{$accountId}/c/{$clientFolderId}/contacts/{$contactId}",	'DELETE', array(		array()	));if ($response['code'] == STATUS_CODE_SUCCESS) {	//echo "<h1>Success</h1>\n";		$contactId = $response['data']['contacts'][0]['contactId'];		$warningCount = 0;	if (!empty($response['data']['warnings'])) {		$warningCount = count($response['data']['warnings']);	}		alert ("<p><b>Success!</b> Deleted contact {$contactId}, with {$warningCount} warnings.</p>", true);		//dump($response['data']);} else {	//echo "<h1>Error</h1>\n";		alert ("<p><b>Error Code:</b> {$response['code']}</p>", false);		//dump($response['data']);}?>