<?php/* * Gets the history of actions a contact has performed */require_once '../cms/icontact/config.php';require_once '../cms/icontact/util.php';/***********************************//** set your account details here **//***********************************///$accountId      = 1001413;//$clientFolderId = 9196527;//$contactId      = 1049733100;$response = callResource("/a/{$accountId}/c/{$clientFolderId}/contacts/{$contactId}/actions", 'GET');if ($response['code'] == STATUS_CODE_SUCCESS) {	//echo "<h1>Success</h1>\n";		$actions = $response['data']['actions'];	$count    = count($actions);	$total    = $response['data']['total'];		$warningCount = 0;	if (!empty($response['data']['warnings'])) {		$warningCount = count($response['data']['warnings']);	}		alert ("<p><b>Success!</b> Got {$count} of {$total} actions, with {$warningCount} warnings.</p>", true);		dump($response['data']);} else {	//echo "<h1>Error</h1>\n";		alert ("<p><b>Error Code:</b> {$response['code']}</p>", false);		dump($response['data']);}?>