<?php/* * Sends a message to a list */require_once '../cms/icontact/config.php';require_once '../cms/icontact/util.php';/***********************************//** set your account details here **//***********************************///$accountId      = 1001413;//$clientFolderId = 9196527;//$messageId      = 206870;//$listId         = 8485;$response = callResource("/a/{$accountId}/c/{$clientFolderId}/sends",	'POST', array(		array(			'messageId'      => $messageId,			'includeListIds' => $listId,		)	));if ($response['code'] == STATUS_CODE_SUCCESS) {	//echo "<h1>Success</h1>\n";		$sends = $response['data']['sends'];	$sent = !empty($sends);	if ($sent) {		$recipientCount = $sends[0]['recipientCount'];	}		$warningCount = 0;	if (!empty($response['data']['warnings'])) {		$warningCount = count($response['data']['warnings']);	}		if ($sent) {		alert ("<b>Success!</b> <p>The message was sent to {$recipientCount} recipients, with {$warningCount} warnings.</p>", true);	} else {		alert ("<p><b>Error!</b> The message was not sent, see the {$warningCount} warnings below.</p>", false);	}		dump($response['data']);} else {	//echo "<h1>Error</h1>\n";		alert ("<p><b>Error Code:</b> {$response['code']}</p>", false);		dump($response['data']);}?>